    public static int calcFloor(String s) {
        int floor = 0;
        int firstBasement = 0;
        for (int i = 0; i < s.length(); i++) {
            if (s.charAt(i) == '(') {
                floor++;
            } else if (s.charAt(i) == ')') {
                floor--;
                if (floor < 0) {
                    firstBasement = i+1;
                    break;
                }
            }
        }
        return firstBasement;
    }
